package week1.day1;

public class AssignemnetCheckPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 0;
		if (number > 0) {
			System.out.println("It's positive number");
		} else if (number < 0) {
			System.out.println("It's negative number");
		} else {
			System.out.println("It's neither positive or negative");
		}
	}

}
